mapper.select:
select * from goods where catelog_id = #{catelog_id,jdbcType=INTEGER}
AND (name like concat('%',#{name},'%')
OR describle like concat('%',#{describle},'%'))

select * from goods
where catelog_id = #{catelogId,jdbcType=INTEGER}
and good_status in (1,3)
order by polish_time DESC
limit #{limit,jdbcType=INTEGER}

mapper.update:
update goods

set good_status = 3

where id = #{id,jdbcType=INTEGER}

update goods
set good_status =#{0}
where id = #{1}
